import React from "react";
import "./App.css";
import { Link, NavLink, Route, Routes } from "react-router-dom";
import { Home } from "./pages/Home";
import { Portfolio } from "./pages/Portfolio";

function App() {
  return (
    <>
      <NavLink className="navigation flex" to={"/"}>
        <header className="cd-header">
          <div className="header-wrapper">
            <div className="logo-wrap">
              <img
                className={process.env.PUBLIC_URL + '/gr_icon.svg'}
                src='/gr_icon.png'
                alt="logo"
              />
            </div>
            <div className="nav-but-wrap">
              <div className="menu-icon hover-target">
                <span className="menu-icon__line menu-icon__line-left"></span>
                <span className="menu-icon__line"></span>
                <span className="menu-icon__line menu-icon__line-right"></span>
              </div>
            </div>
          </div>
        </header>

        <div className="nav__content">
          <ul className="nav__list">
            <li>
              <Link to={"/"} className="nav__list-item">
                Home
              </Link>
            </li>
            <li>
              <Link to={"/"} className="nav__list-item">
                About
              </Link>
            </li>
            <li>
              <Link to={"/portfolio"} className="nav__list-item">
                Portfolio
              </Link>
            </li>
            <li>
              <Link to={"/"} className="nav__list-item">
                Contact
              </Link>
            </li>
          </ul>
        </div>
      </NavLink>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/portfolio" element={<Portfolio />} />
      </Routes>
    </>
  );
}

export default App;

//Navigation
var app = (function () {
  var body = undefined;
  var menu = undefined;
  var menuItems = undefined;
  var init = function init() {
    body = document.querySelector("body");
    menu = document.querySelector(".menu-icon");
    menuItems = document.querySelectorAll(".nav__list-item");
    applyListeners();
  };

  var applyListeners = function applyListeners() {
    menu.addEventListener("click", function () {
      return toggleClass(body, "nav-active");
    });
  };

  var toggleClass = function toggleClass(element, stringClass) {
    if (element.classList.contains(stringClass))
      element.classList.remove(stringClass);
    else element.classList.add(stringClass);
  };

  init();
})();

//Nav clicks
var menuItems = document.querySelectorAll(".nav__list-item");
var stringClass = "nav-active";
var element = document.querySelector(".nav__list-item");

$(menuItems).on("click", function () {
  if ($("body").hasClass("nav-active")) {
    $("body").removeClass("nav-active");
  }
  if (element.classList.contains(stringClass))
    element.classList.remove(stringClass);
  else element.classList.add(stringClass);
});
