import React, { useState } from 'react';

const TypingText = ({ strings }) => {
  const [typingText, setTypingText] = useState('');
  const [stringIndex, setStringIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);

  // Use an interval to update the typing text
  useInterval(() => {
    // Get the current string and character indexes
    const currentString = strings[stringIndex];
    const currentChar = currentString.charAt(charIndex);

    // Append the current character to the typing text
    setTypingText(typingText + currentChar);

    // Move to the next character
    setCharIndex(charIndex + 1);

    // If we've reached the end of the current string, move to the next string
    if (charIndex + 1 >= currentString.length) {
      setStringIndex(stringIndex + 1);
      setCharIndex(0);
    }
  }, 100); // Update the text every 100ms

  return <div>{typingText}</div>;
}

// Custom hook to handle the interval for updating the typing text
function useInterval(callback: number, delay: number) {
  const savedCallback = useRef();

  // Remember the latest callback.
  useEffect(() => {
    savedCallback.current = callback;
  }, [callback]);

  // Set up the interval.
  useEffect(() => {
    function tick() {
      savedCallback.current();
    }
    if (delay !== null) {
      let id = setInterval(tick, delay);
      return () => clearInterval(id);
    }
  }, [delay]);
}


export default TypingText;
