import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "./NavBar.css";

function NavBar() {
  const [click, setClick] = useState(false);

  const toggleClass = function toggleClass(element:Element, stringClass:string) {
    if (element.classList.contains(stringClass))
      element.classList.remove(stringClass);
    else element.classList.add(stringClass);
  };

  const handleClick = () => {
    var lineOne = Array.from(document.getElementsByClassName("menu-icon__line"))[0];
    var lineTwo = Array.from(document.getElementsByClassName("menu-icon"))[1];
    var lineThree = Array.from(document.getElementsByClassName("menu-icon"))[2];
    toggleClass(lineOne, "menu_icon_line_one");
    toggleClass(lineTwo, "menu_icon_line_two");
    toggleClass(lineThree, "menu_icon_line_three");
    setClick(!click);
  };
  return (
    <>
      <nav className="navbar">
        <div className="nav-container">
          <NavLink to="/" className="nav-logo">
            <img
              src={process.env.PUBLIC_URL + "/gr_icon_plain.svg"}
              alt="logo"
              className="navbar-logo"
            ></img>
          </NavLink>

          <ul className={click ? "nav-menu active" : "nav-menu"}>
            <li className="nav-item">
              <NavLink to="/" className="nav-links" onClick={handleClick}>
                Home
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink to="/" className="nav-links" onClick={handleClick}>
                About
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink
                to="/portfolio"
                className="nav-links"
                onClick={handleClick}
              >
                Portfolio
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink to="/" className="nav-links" onClick={handleClick}>
                Contact Us
              </NavLink>
            </li>
          </ul>
          <div className="nav-icon" onClick={handleClick}>
            {/* <i className={click ? "fas fa-times" : "fas fa-bars"}></i> */}

            <div className="menu-icon hover-target">
              <span className="menu-icon__line menu-icon__line-left"></span>
              <span className="menu-icon__line"></span>
              <span className="menu-icon__line menu-icon__line-right"></span>
            </div>
          </div>
        </div>
      </nav>
    </>
  );
}

export default NavBar;
