import { useEffect, useState } from "react";
import { TypeAnimation } from "react-type-animation";
import "./Home.css";

export function Home() {
  // const typingDelay: number = 1000;

  const [playAnimation, setPlayAnimation] = useState(false);

  // This will run one time after the component mounts
  useEffect(() => {
    setPlayAnimation(true);
  }, []);

  var typingText: string = "";

  const textArray: string[] = [
    "is looking for internships",
    "likes problem solving",
    "is studying in Exeter",
    "loves the gym!",
    "wants to create iOS apps",
    "is looking for jobs",
    "wants to get into data science",
    "loves to draw",
  ];

  const typingDelay: number = 100;
  const erasingDelay: number = 60;
  const newTextDelay: number = 2000; // Delay between current and next text
  let textArrayIndex: number = 0;
  let charIndex: number = 0;
  const isTyping: boolean = true;

  function type() {
    if (charIndex < textArray[textArrayIndex].length) {
      typingText += textArray[textArrayIndex].charAt(charIndex);

      charIndex++;
      setTimeout(type, typingDelay);
    } else {
      setTimeout(erase, newTextDelay);
    }
  }

  function erase() {
    if (charIndex > 0) {
      typingText = textArray[textArrayIndex].substring(0, charIndex - 1);
      charIndex--;
      setTimeout(erase, erasingDelay);
    } else {
      textArrayIndex++;
      if (textArrayIndex >= textArray.length) textArrayIndex = 0;
      setTimeout(type, typingDelay + 1100);
    }
  }

  document.addEventListener("DOMContentLoaded", function () {
    // On DOM Load initiate the effect
    if (textArray.length) setTimeout(type, newTextDelay + 250);
  });



  return (
    <div className="home-page flex">
      <div className="intro-info">
        <div className="title plax">
          I'm <span>Gowtham</span>
          <span className="title-span">.</span>
        </div>
        <div className="title-text plax">
          A programmer who <span className="typed-text"> </span>
          <span className="typingText">{typingText}</span>
