import "./Home.css";

export function Home() {
  const typedTextSpan = document.querySelector(".typed-text");
  const cursorSpan = document.querySelector(".typed-text-cursor");

  const textArray = [
    "is looking for internships",
    "likes problem solving",
    "is studying in Exeter",
    "loves the gym!",
    "wants to create iOS apps",
    "is looking for jobs",
    "wants to get into data science",
    "loves to draw",
  ];

  const typingDelay = 100;
  const erasingDelay = 60;
  const newTextDelay = 2000; // Delay between current and next text
  let textArrayIndex = 0;
  let charIndex = 0;

  function type() {
    if (charIndex < textArray[textArrayIndex].length) {
      if (!cursorSpan!.classList.contains("typing"))
        cursorSpan!.classList.add("typing");
      typedTextSpan!.textContent += textArray[textArrayIndex].charAt(charIndex);
      charIndex++;
      setTimeout(type, typingDelay);
    } else {
      cursorSpan!.classList.remove("typing");
      setTimeout(erase, newTextDelay);
    }
  }

  function erase() {
    if (charIndex > 0) {
      if (!cursorSpan!.classList.contains("typing"))
        cursorSpan!.classList.add("typing");
      typedTextSpan!.textContent = textArray[textArrayIndex].substring(
        0,
        charIndex - 1
      );
      charIndex--;
      setTimeout(erase, erasingDelay);
    } else {
      cursorSpan.classList.remove("typing");
      textArrayIndex++;
      if (textArrayIndex >= textArray.length) textArrayIndex = 0;
      setTimeout(type, typingDelay + 1100);
    }
  }

  return (
    <div className="home-page flex">
      <div className="intro-info">
        <div className="title plax">
          I'm <span>Gowtham</span>
          <span className="title-span">.</span>
        </div>
        <div className="title-text plax">
          A programmer who <span className="typed-text"> </span>
          <span className="typed-text-cursor">|</span>
        </div>
      </div>
    </div>
  );
}
